import React from "react";

const API_URL = "https://pokeapi.co/api/v2/berry/{id or name}/";

export default class Berry extends React.Component {

    render () {
        // utilise la props (les data de l'API), les affiche, teste qu'elles sont récupéré
        const { data } = this.props;
        console.log("data", data);
        //if (!data) return null;

        // get API data
        const name = data.data.name;
        const growth_time = parseFloat(data.data.growth_time);
        const max_harvest = parseFloat(data.data.max_harvest);
        const natural_gift_power = parseFloat(data.data.natural_gift_power);
        //const sprite = data.data.sprites.front_default;
       
        // affichage des infos
        return (
            <div class='berry'>
                <div class='profileInfos'>
                    <div class='profileInfo'>
                        {`Nom: ${name}`}
                    </div>
                    <div class='profileInfo'>
                        {`Temps de pousse: ${growth_time} h`}
                    </div>
                    <div class='profileInfo'>
                        {`Nombre de récolte: ${max_harvest}`}
                    </div>
                    <div class='profileInfo'>
                        {`Puissance de Natural Gift: ${natural_gift_power}`}
                    </div>
                    {/*<div>
                        <img
                            src={sprite}
                        />
                    </div>*/}
                </div>
            </div>
        )
    }

}
