import React from 'react';
import Button from '../components/button';
import '../index.css';

import 'bootstrap/dist/css/bootstrap.min.css';

export default class Nav extends React.Component {
    callAPI = () => {
        // Call API
        axios
          .get(`${API_URL}/`)
          .then(({ data }) => {
            console.log(data);
            // Recupere uniquement la propriété data
            const dataPoke = { data };
            
            this.setState({ dataPoke });
          })
          .catch(console.error);
      };

    // Lance un appel au lancement du component
    componentDidMount() {
        this.callAPI();
    }

    // A chaque update relance une api
    componentDidUpdate(nextProps) {
      // Ici on verifie que la mise à jour concerne bien le champs city
      if (nextProps !== this.props.id) {
        this.callAPI(nextProps.id);
      }
    }
    render() {
        const { users, changeUser } = this.props;

        return (
            <div class="container" id="listedresseur">
                <div class="row">
                    {users.map((user) => <Button text={user.name} onClick={() => changeUser(user.name)} />)}
                </div>
            </div>)
    }
}
