import React from 'react';
import Berry from '../components/berry';
import Pokemon from '../components/pokemon';
import Item from '../components/item';
import Skill from '../components/skills';
import '../index.css';
import FichePokemon from './DataPokedex';

// configuration Pokedex-promise-V2
import Pokedex from 'pokedex-promise-v2';
const options = {
  protocol: 'https',
  hostName: 'localhost:443',
  versionPath: '/api/v2/',
  cacheLimit: 100 * 1000, // 100s
  timeout: 5 * 1000 // 5s
}

export default class Poke extends React.Component {
    state ={
        pokemon : 'pikachu',
        berry : '', 
        item : '', 
        skill : ''
    };

    handleChangePokemon = event => {
        this.setState({ pokemon: event.target.value });
    };

    render() {
        let { pokemon, berry, item, skill, response} = this.props;
        // Utilisation API
        const P = new Pokedex(options);
        P.getPokemonByName(pokemon)
        .then((response) => {
          console.log(response);
        })
        .catch((error) => {
          console.log('There was an ERROR: ', error);
        });
    

        return (
            <div>
                <div class='Pokemon'>
                    <h2> Recherche Pok√©mon </h2> 
                    <input 
                        type='text'
                        value={ pokemon }
                        onChange={ this.handleChangePokemon }
                    />
                    <div class='pokemonInfo'>
                        <Pokemon data={ response } />
                    </div>
                </div>

                <div class="Berry">
                    <h2> Recherche Berry </h2> 
                    <input 
                        type='text'
                        name='Berry'
                        value={berry}
                    />
                    <div class='berryInfo'>
                        <Berry berry/>
                    </div>
                </div>

                <div class="Items">
                    <h2> Recherche Object </h2> 
                    <input 
                        type='text'
                        name='Item'
                        value={item}
                    />
                    <div class='itemInfo'>
                        <Item item/>
                    </div>
                </div>

                <div class="Skill">
                    <h2> Recherche Attaque/Move </h2> 
                    <input 
                        type='text'
                        name='skill'
                        value={skill}
                    />
                    <div class='skillInfo'>
                        <Skill skill/>
                    </div>
                </div>

            </div>
        )
    }
}
