import React from 'react';
import '../index.css';

const API_URL = "";

export default class Pokemon extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            name : 'Pikachu',
            height : '0',
            weight : '1',
            type : 'electric'
        };
    }

    
    render () {
        const { pokemon } = this.props;

        return (
            <div class='Pokemon'>
                <div class='infoPokemon'>
                    {`Nom : ${this.name}`}
                </div>
                <div class='infoPokemon'>
                    {`Taille : ${this.height}`}
                </div>
                <div class='infoPokemon'>
                    {`Poids : ${this.weight}`}
                </div>
                <div class='infoPokemon'>
                    {`Type : ${this.type}`}
                </div>
            </div>
        );
    }
}
