const API_URL = "https://pokeapi.co/api/v2/berry/{id or name}/";

export default class Berry extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            name: 'name',
            growth_time: '0',
            max_harvest: '0',
            natural_gift_power: '0'
        }
    }

    render() {
        let berry = this.state;

        return ( <
            div class = 'berry' >
