import React from 'react';
import '../index.css';

const API_URL = "";

export default class Pokemon extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            name : 'Pikachu',
            height : '0',
            weight : '1',
            type : 'electric'
        }
    }
    
    render () {
        let pokemon = this.state;

        return (
            <div class='Pokemon'>
                <div class='infoPokemon'>
                    {`Nom : ${pokemon.name}`}
                </div>
                <div class='infoPokemon'>
                    {`Taille : ${pokemon.height}`}
                </div>
                <div class='infoPokemon'>
                    {`Poids : ${pokemon.weight}`}
                </div>
                <div class='infoPokemon'>
                    {`Type : ${pokemon.type}`}
                </div>
            </div>
        );
    }
}
