import React from 'react';
import User from './components/user';
import Nav from './containers/nav';
import './App.css';
import GuideMeteo from './containers/guideMeteo';
import Pokedex from './containers/pokedex';

import 'bootstrap/dist/css/bootstrap.min.css';


import { Link } from 'react-router-dom';
import Chemin from './chemin';




export default class App extends React.Component {
    
    constructor (props){
        super(props);
        this.state={
            city: 'Paris'
        }
        
    }
    

    render() {
       
        return (
            <div class='App container'>
            <div class="row">
                <aside class="col-sm-3" id="aside">
                    
                    <h1 class='ecriture'>Poke<span class='base'>Ocres</span></h1>
                    <ul class="nav nav-pills flex-column">
                        <li class="pills">
                        <Link class="nav-link " to="/">Home</Link>
                        <br></br>
                        </li>
                        <li class="pills">
                        <Link class="nav-link " to="/parametres">Parametres</Link>
                        </li>
                    </ul>
                    <div class="headshot headshot-meteo col ">
                            <div class='GuideMeteoGO'>
                                <h2> Guide Méteo pour jouer à GO </h2>
                                <input
                                    id="meteo"
                                    type="text"
                                    name="Ville"
                                    value={this.state.city}
                                    onChange={this.handleChange}
                                />
                                <GuideMeteo city={this.state.city} />
                            </div>
                        </div>
                </aside>
                <div class="col-sm-9">
                    <main class="container">
                        <Chemin/>
                    </main>
                </div>
            </div>
        </div>
        );
    }

}
